import { Component } from '@angular/core';
import { MenuItem } from '../../interfaces/menu-item.interface';

@Component({
  selector: 'shared-side-menu',
  templateUrl: './side-menu.component.html',
  styleUrls: ['./side-menu.component.css']
})
export class SideMenuComponent {

  public reactiveMenu: MenuItem[] = [
    {title: 'Basicos' , route: './reactive/basic'},
    {title: 'Dinamicos' , route: './reactive/dynamic'},
    {title: 'Switches' , route: './reactive/switches'},
  ]

  public authMenu: MenuItem[] = [
    {title: 'Registro' , route: './auth/sign-up'},
  ]
}
<h2>Páginas</h2>
<hr>
<h3 class="mt-3">Reactive</h3>

<ul class="list-group">
  <li *ngFor="let item of reactiveMenu" class="list-group-item" [routerLink]="item.route" routerLinkActive="active">
    {{ item.title }}
  </li>
</ul>

<h3 class="mt-3">Validaciones</h3>

<ul class="list-group">
  <li *ngFor="let item of authMenu" class="list-group-item" [routerLink]="item.route" routerLinkActive="active">
    {{ item.title }}
  </li>
</ul>
