import { Injectable } from '@angular/core';
import { FormControl, FormGroup, ValidationErrors } from '@angular/forms';

@Injectable({providedIn: 'root'})
export class ValidatorsService {
  constructor() { }

  public cantBeStrider = (control: FormControl): ValidationErrors | null => {
    const value = control.value.trim().toLowerCase();
    if (value === 'strider') {
      return {
        noStrider: true
      }
    }
    return null // si no hay error
  }



  public firstNameAndLastnamePattern: string = '([a-zA-Z]+) ([a-zA-Z]+)';
  public emailPattern: string = "^[a-z0-9._%+-]+@[a-z0-9.-]+\\.[a-z]{2,4}$";

  public isValidField(field: string, form : FormGroup) {
    return (form.controls[field].errors && form.controls[field].touched)
  }

  isFieldOneEqualFieldTwo(field1: string, field2: string) {
    return (formGroup: FormGroup) : ValidationErrors | null => {
      const fieldValue1 = formGroup.controls[field1].value;
      const fieldValue2 = formGroup.controls[field2].value;
      if (fieldValue1 !== fieldValue2) {
        formGroup.controls[field2].setErrors({noEqual: true});
        return {noEqual: true};
      }
      formGroup.controls[field2].setErrors(null);
      return null;
    }
  }
}
