import { FormControl, ValidationErrors } from "@angular/forms";

export const cantBeStrider = (control: FormControl): ValidationErrors | null => {
  const value = control.value.trim().toLowerCase();
  if (value === 'strider') {
    return {
      noStrider: true
    }
  }
  return null // si no hay error
}
