validate(control: AbstractControl<any, any>): Promise<ValidationErrors | null> | Observable<ValidationErrors | null> {
  const email = control.value;
  return of({
    emailTaken: {
      message: 'This email is already taken'
    }
  }).pipe(
    delay(3500)
  );
}
}
